/****************************************************************************
 *	McDevProf_GetRegAccess
 *
 *	Description:
 *			Get register access availability
 *	Arguments:
 *			psRegInfo	register information
 *	Return:
 *			MCDRV_REG_ACCSESS
 *
 ****************************************************************************/
MCDRV_REG_ACCSESS	McDevProf_GetRegAccess
(
	const MCDRV_REG_INFO*	psRegInfo
)
{
	MCDRV_REG_ACCSESS	eAccess	= eMCDRV_ACCESS_DENY;
	MCDRV_DEV_ID		eDevID	= geDevID;

#if (MCDRV_DEBUG_LEVEL>=4)
	SINT32	sdRet;
	McDebugLog_FuncIn("McDevProf_GetRegAccess");
#endif

	if(geDevID == eMCDRV_DEV_ID_46_16H)
	{
		eDevID	= eMCDRV_DEV_ID_46_14H;
	}
	else if(geDevID == eMCDRV_DEV_ID_44_16H)
	{
		eDevID	= eMCDRV_DEV_ID_44_14H;
	}
	else if(geDevID == eMCDRV_DEV_ID_46_15H)
	{
		eDevID	= eMCDRV_DEV_ID_46_14H;
	}
	else if(geDevID == eMCDRV_DEV_ID_44_15H)
	{
		eDevID	= eMCDRV_DEV_ID_44_14H;
	}
	else
	{
	}

	switch(psRegInfo->bRegType)
	{
	case	MCDRV_REGTYPE_A:
		eAccess	= gawRegAccessAvailableA[eDevID][psRegInfo->bAddress];
		break;
	case	MCDRV_REGTYPE_B_BASE:
		eAccess	= gawRegAccessAvailableB_BASE[eDevID][psRegInfo->bAddress];
		break;
	case	MCDRV_REGTYPE_B_ANALOG:
		eAccess	= gawRegAccessAvailableB_ANA[geDevID][psRegInfo->bAddress];
		break;
	case	MCDRV_REGTYPE_B_CODEC:
		eAccess	= gawRegAccessAvailableB_CODEC[geDevID][psRegInfo->bAddress];
		break;
	case	MCDRV_REGTYPE_B_MIXER:
		eAccess	= gawRegAccessAvailableB_MIX[eDevID][psRegInfo->bAddress];
		break;
	case	MCDRV_REGTYPE_B_AE:
		eAccess	= gawRegAccessAvailableB_AE[eDevID][psRegInfo->bAddress];
		break;
	case	MCDRV_REGTYPE_B_CDSP:
		if((eDevID == eMCDRV_DEV_ID_46_14H) || (eDevID == eMCDRV_DEV_ID_44_14H))
		{
			eAccess	= gawRegAccessAvailableB_CDSP[psRegInfo->bAddress];
		}
		break;
	default:
		break;
	}

#if (MCDRV_DEBUG_LEVEL>=4)
	sdRet	= eAccess;
	McDebugLog_FuncOut("McDevProf_GetRegAccess", &sdRet);
#endif

	return eAccess;
}



